mapper.select:
SELECT * FROM t_contest_info
WHERE contest_kind = #{kind}

    AND title LIKE #{searchTitle}


    AND permission_type = #{searchPermission}


    AND begin_time > NOW()


    AND NOW() > begin_time
    AND end_time > NOW()


    AND NOW() > end_time

ORDER BY contest_id DESC, begin_time DESC, end_time DESC
LIMIT #{startIndex}, #{pageSize};

SELECT COUNT(*)
FROM t_contest_info
WHERE contest_kind = #{kind}

    AND title LIKE #{searchTitle}


    AND permission_type = #{searchPermission}


    AND begin_time > NOW()


    AND NOW() > begin_time
    AND end_time > NOW()


    AND NOW() > end_time

;
