mapper.update:
UPDATE t_problem_info
SET total_submit = total_submit + 1
WHERE problem_id = #{problemId};

UPDATE t_problem_info
SET total_ac = total_ac + 1
WHERE problem_id = #{problemId};

UPDATE t_problem_info
SET total_submit_user = total_submit_user + 1
WHERE problem_id = #{problemId};

UPDATE t_problem_info
SET total_ac_user = total_ac_user + 1
WHERE problem_id = #{problemId};

mapper.select:
SELECT COUNT(*) FROM t_problem_info WHERE 1=1
AND title LIKE #{title}
AND problem_id IN (SELECT DISTINCT problem_id FROM t_problem_tag_record WHERE tag_id=#{tagId}) ;
