mapper.update:
UPDATE t_user_auth
SET attempt_login_fail_count = attempt_login_fail_count + #{times}
WHERE username = #{username}

UPDATE t_user_auth
SET attempt_login_fail_count = 0
WHERE username = #{username}

UPDATE t_user_auth
SET unlock_time = #{unlockTime}
WHERE username = #{username}

mapper.select:
SELECT COUNT(*)
FROM t_user_auth
WHERE username = #{username}

SELECT attempt_login_fail_count
FROM t_user_auth
WHERE username = #{username}
